sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (newValue === '') {
		return;
	}


	//var test = NeedItWhatNeededDisplayValue.getDisplayValofCh("hr1");
	//alert (test);
	//Type appropriate comment here, and begin script below
	var whatneeded = g_form.getValue('u_what_needed');

	var ga = new GlideAjax('NeedItWhatNeededDisplayValue'); // HelloWorld is the script include class
	ga.addParam('sysparm_name','getDisplayValofCh');
	ga.addParam('chval', whatneeded);

	console.log("Test this script by Rashid");


	try{
		helloWorldnew();
	}
	catch(err){
		jslog('A JavaScript runtime error occurred: ' + err.message);
	}

	var disVal = g_form.getOption('u_what_needed' , whatneeded).text;	
	g_form.clearOptions('u_what_needed');

	if (newValue == 'hr'){
		jslog ('HR Activated');
		g_form.addOption('u_what_needed', 'hr1' , 'Human Resources 1');
		g_form.addOption('u_what_needed', 'hr2' , 'Human Resources 2');
		g_form.addOption('u_what_needed', 'other' , 'Other');
	}else if (newValue == 'facilities'){
		jslog ('FL Activated');
		g_form.addOption('u_what_needed', 'facilities1' , 'Facilities 1');
		g_form.addOption('u_what_needed', 'facilities2' , 'Facilities 2');
		g_form.addOption('u_what_needed', 'other' , 'Other');
	}else if (newValue == 'legal'){
		jslog ('Legal Activated');
		g_form.addOption('u_what_needed', 'legal1' , 'Legal 1');
		g_form.addOption('u_what_needed', 'legal2' , 'Legal 2');
		g_form.addOption('u_what_needed', 'other' , 'Other');
	}

	ga.getXML(getChoiseDispVal);
	function getChoiseDispVal(response) {
		var labelVal = response.responseXML.documentElement.getAttribute("answer");		

		if (isLoading || !g_form.isNewRecord()){
			jslog ('Not new record activated');			
			//g_form.setValue('u_what_needed' , whatneeded, disVal);
			g_form.setValue('u_what_needed' , whatneeded, labelVal);
		}
	}

}
