function validEmailAdd(emailVal) {
	//var r = new RegExp('/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/', 'jg');
	//var str = 'helloworld';
	//var replaced = str.replace(r, 'there');

	var r = new RegExp(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/);

	var emailStr = emailVal + '';
		// Compare emailStr against the allowed syntax as specified in the regular expression
		// If emailStr has allowed syntax, return true, else return false
		if(emailStr.match(r)){
			return true;
		}
		else {
		 return false;
		}


}
