sys_script_include.script:
var UpdateRequestedForEmail = Class.create();
UpdateRequestedForEmail.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getEmail:function(){
		var userIdVal = this.getParameter('userId');
		var gr = new GlideRecord('sys_user');
		gr.get(userIdVal);
		return gr.email;
	},

	getEmailNew:function(){
		var userIdVal = this.getParameter('userId');
		var gr = new GlideRecord('sys_user');
		gr.get(userIdVal);
		return gr.email + "New";
	},

    type: 'UpdateRequestedForEmail'
});
