var NeedItWhatNeededDisplayValue = Class.create();
NeedItWhatNeededDisplayValue.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getDisplayValofCh:function(){
		var pr1 = this.getParameter('chval');
		var rval = pr1;
		gr = new GlideRecord('sys_choice');
		gr.addQuery('name','=','x_58872_needit_needit');
		gr.addQuery('element','=','u_what_needed');
		gr.addQuery('value','=', pr1);
		gr.query();
		while (gr.next()){
			rval = gr.label;
		}

		return rval;
	},

	type: 'NeedItWhatNeededDisplayValue'
});
